import styled from 'styled-components';

export const StatSaction = styled.section`
  width: 400px;
  margin: 0 auto;
  background-color: #fff;
  text-align: center;
`;

export const StatTitle = styled.h2`
  padding: 20px 0;
  color: #a2a2a2;
`;

export const StatList = styled.ul`
  display: flex;
  flex-d: column;
`;
