import { styled } from 'styled-components';

export const TableHeaderStyle = styled.thead``;

export const TableHeaderTrStyle = styled.tr`
  width: 100%;
`;

export const TableHeaderItemStyle = styled.th`
  background-color: #5ca0c6;
  padding: 5px 0;
  width: 100%;
  flex-grow: 1;
`;
