import { styled } from 'styled-components';

export const TableContainer = styled.table`
  width: 450px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  box-shadow: 1px -1px 16px 0px rgba(0, 0, 0, 0.76);
`;
