import { styled } from 'styled-components';

export const TableBodyTr = styled.tr`
  width: 100%;
  display: flex;
  justify-content: space-around;
  fle-grow: 1;
`;

export const TableBodyTd = styled.td`
  flex-grow: 1;
  padding: 5px 0;
  width: 100%;
  text-align: center;
  background-color: #fff;
  :hover {
    box-shadow: 1px -1px 16px 0px rgba(0, 0, 0, 0.76);
  }
`;
