import { styled } from 'styled-components';

export const FriendsStyleItem = styled.li`
  display: flex;
  background-color: #fff;
  padding: 10px 0;
`;

export const FriendsStyleSpan = styled.span`
display: 'block',
width: 15px;
bopder-radius: 50%;
color: red;
`;
