export const FriendsItem = ({ friends }) => {
  return friends.map(({ avatar, name, isOnline, id }) => {
    return (
      <li class="item">
        <span class="status"></span>
        <img class="avatar" src="" alt="User avatar" width="48" />
        <p class="name"></p>
      </li>
    );
  });
};
