import styled from 'styled-components';

export const Wrapper = styled.div``;

export const ImageUser = styled.img`
  width: 200px;
  border-radius: 50%;
  background-color: #fff;
`;
export const Text = styled.p`
  font-size: ${props => {
    props.color;
  }};
`;
