import { styled } from 'styled-components';

export const FriendsStyleItem = styled.li`
  display: flex;
  align-items: center;
  background-color: #fff;
  padding: 10px 0;
`;

export const FriendsStyleSpan = styled.span`
  display: block;
  width: 10px;
  height: 10px;
  margin: 0 10px;
  border-radius: 50%;
  background-color: red;
`;

export const FriendsStyleImg = styled.img``;
