import styled from 'styled-components';

export const SocialList = styled.ul`
  display: flex;
  justify-content: center;
  gap: 16px;
  background-color: #f0f0f0;
  padding: 20px 0;
`;

export const SocialItem = styled.li`
  display: flex;
  flex-direction: column;
`;
